import{_ as c}from"./plugin-vue_export-helper-c27b6911.js";import{r as a,o as i,c as l,a as _,e,d as o,w as s,f as t,b as d}from"./app-d2020c50.js";const h={},p=e("p",null,"第一章：500 Lines or Less 介绍。",-1),u={class:"table-of-contents"},m={class:"hint-container info"},f=e("p",{class:"hint-container-title"},"关于翻译",-1),k={href:"http://aosabook.org/en/500L/archives.html",target:"_blank",rel:"noopener noreferrer"},b=d('<h2 id="本书介绍" tabindex="-1"><a class="header-anchor" href="#本书介绍" aria-hidden="true">#</a> 本书介绍</h2><p>这是 <em>开源应用程序架构</em> 系列的第四卷，也是第一卷在标题中没有 <strong>开源应用程序</strong> 一词的卷。</p><p>该系列的前三卷是关于大程序必须解决的大问题。对于处于职业生涯早期的工程师来说，理解和构建比几千行代码大得多的程序可能是一个挑战，因此，虽然阅读大问题可能很有趣，但从中学习也可能具有挑战性。</p><p><em>500 Lines or Less</em> 专注于程序员在构建新事物时做出的设计决策。你将在本书中读到的程序都是为此目的从头开始编写的（尽管其中有几个程序的灵感来自作者以前参与过的大型项目）。</p><p>在阅读每一章之前，我们鼓励您首先考虑如何解决问题。您认为作者将考虑哪些重要的设计考虑因素或约束？你希望看到哪些抽象？你认为这个问题将如何分解？然后，在阅读本章时，尝试找出让你感到惊讶的地方。我们希望您能通过这样做学到更多，而不仅仅是从头到尾阅读每一章。</p><p>用不到 500 行的源代码编写一个有用的程序 —— 不诉诸廉价的技巧 —— 本身就是一项具有挑战性的工作；当整齐地呈现在印刷书中时，写一个用于教学目的的阅读甚至更加困难。因此，编辑在将一些源代码格式移植到书中时偶尔会自由选择。每个章节的原始源代码可以在其项目文件夹的子目录中找到。</p><blockquote><p>我们希望本书作者的经历能帮助你在自己的编程实践中走出舒适区。<br><cite>—— 迈克尔·迪贝尔纳多</cite></p></blockquote><h2 id="贡献" tabindex="-1"><a class="header-anchor" href="#贡献" aria-hidden="true">#</a> 贡献</h2>',8),L={href:"http://aosabook.org/en/500L/introduction.html",target:"_blank",rel:"noopener noreferrer"};function x(g,v){const n=a("router-link"),r=a("ExternalLinkIcon");return i(),l("div",null,[p,_(" more "),e("nav",u,[e("ul",null,[e("li",null,[o(n,{to:"#本书介绍"},{default:s(()=>[t("本书介绍")]),_:1})]),e("li",null,[o(n,{to:"#贡献"},{default:s(()=>[t("贡献")]),_:1})])])]),e("div",m,[f,e("p",null,[t("本系列翻译自 "),e("a",k,[t("500 Lines or Less"),o(r)]),t("，部分翻译内容参考网络上的其他翻译，包含机翻，故不能保证准确性，如果您发现了错误，欢迎指正。")])]),b,e("p",null,[t("请访问 "),e("a",L,[t("原文"),o(r)]),t("。关于感谢、说明等信息也可以在原文找到。")])])}const B=c(h,[["render",x],["__file","introduction.html.vue"]]);export{B as default};
